import React, { useState } from "react";

export default function VarenukBlock(props) {
  const [varenukCount, setVarenukCount] = React.useState(0);

  const onClickAddButton = () => {
    return setVarenukCount;
  };
  return (
    <div class="varenuk-block">
      <img
        width={260}
        height={260}
        class="varenuk-block__image"
        src={props.imageUrl}
        alt="Varenuk"
      />
      <h4 class="varenuk-block__title">{props.title}</h4>
      <div class="varenuk-block__selector">
        <ul>
          <li class="active">тонке</li>
          <li>традиційне</li>
        </ul>
        <ul>
          <li class="active">14 шт.</li>
          <li>24 шт.</li>
          <li>34 шт.</li>
        </ul>
      </div>
      <div class="varenuk-block__bottom">
        <div class="varenuk-block__price">від {props.price} ₴</div>
        <div
          onClick={onClickAddButton}
          class="button button--outline button--add">
          <svg
            width="12"
            height="12"
            viewBox="0 0 12 12"
            fill="none"
            xmlns="http://www.w3.org/2000/svg">
            <path
              d="M10.8 4.8H7.2V1.2C7.2 0.5373 6.6627 0 6 0C5.3373 0 4.8 0.5373 4.8 1.2V4.8H1.2C0.5373 4.8 0 5.3373 0 6C0 6.6627 0.5373 7.2 1.2 7.2H4.8V10.8C4.8 11.4627 5.3373 12 6 12C6.6627 12 7.2 11.4627 7.2 10.8V7.2H10.8C11.4627 7.2 12 6.6627 12 6C12 5.3373 11.4627 4.8 10.8 4.8Z"
              fill="white"
            />
          </svg>
          <span>Добавити</span>
          <i>{onAddVarenuk}</i>
        </div>
      </div>
    </div>
  );
}
