import React from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const FullVarenuk = () => {
  const { varenuk, setVarenuk } = React.useState;
  const { id } = useParams();

  React.useEffect(() => {
    async function ferchVarenuk() {
      const { varenuk } = await axios.get(
        axios.get("https://63ebc7d7be929df00ca23593.mockapi.io/items" + id)
      );
      setVarenuk(varenuk);
    }
  }, []);
  return (
    <div className="container">
      <img src="" alt="" />
      <h2>{id}</h2>
      <p>
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Et non nostrum
        laudantium culpa, doloremque quod optio amet qui hic reprehenderit
        recusandae beatae blanditiis voluptatibus consectetur enim, temporibus
        nisi exercitationem sequi.
      </p>
      <h4>239 грн.</h4>
    </div>
  );
};

export default FullVarenuk;
