import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  item: [],
};

const varenuksSlice = createSlice({
  name: "varenuk",
  initialState,
  reducers: {
    setItems(state, action) {
      state.categoryId = action.payload;
    },
  },
});

export const { setItems } = varenuksSlice.actions;

export default varenuksSlice.reducer;
