import React from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const FullVarenuk = () => {
  const [varenuk, setVarenuk] = React.useState();
  let { id } = useParams();

  React.useEffect(() => {
    async function ferchVarenuk() {
      try {
        const { data } = await axios.get(
          "https://63ebc7d7be929df00ca23593.mockapi.io/items/" + id
        );
        setVarenuk(data);
      } catch (error) {
        alert("Помилка при отриманні вареників");
      }
    }
    ferchVarenuk();
  }, []);

  if (!varenuk) {
    return <>Завантаження...</>;
  }

  return (
    <div className="fullVarenuk">
      <img src={varenuk.imageUrl} alt="Varenuk" />
      <h2>{varenuk.title}</h2>
      <p>Опис вареників.</p>
      <h4>{varenuk.price} грн.</h4>
    </div>
  );
};

export default FullVarenuk;
